script:
let action=this.action.name;
let value=this.action.value;
console.log('Execute action: ',action)
const emdb=API.cache('emdb');
switch (action) {
    case 'ShowGroups': {
        API.require('vh/chemistry/showMfGroupsList').then( shotMfGroupsList => shotMfGroupsList() );
        break;
    }
    case 'autoPeakWidth':
        let peaksWidth=API.cache('peaksWidth');
        if (! peaksWidth) return;

        let fct = 'var width = '+ peaksWidth.A.toPrecision(5) + ' * mass ** '+peaksWidth.B.toPrecision(5) + ';\n';
        fct += 'return {bottom: error + width * 3, top: error + width}';

        let theFunction=new Function('mass',fct); // just to test test

        let preferences=API.getData('preferences');
        preferences.mass.similarity.widthFunction=fct;
        preferences.triggerChange();
        break;
    case 'zoomChanged': {
            if (! emdb) return;
            let currentZoom=this.action.value;
            let previousZoom=API.cache('previousZoom') || {};
            if (previousZoom.from===currentZoom.from && previousZoom.to===currentZoom.to) return;
            API.cache('previousZoom',currentZoom)
            API.doAction('updateAnnotations');
            break;
        }
    case 'labelPrefsChanged':
    case 'updateAnnotations': {
        if (! emdb) return;
        let experimental=emdb.experimentalSpectrum;
        if (! experimental) return;
        let zoom = API.cache('previousZoom')

        let labelPrefs=API.getData('preferences').resurrect().labelPrefs;

        let bestPeaks=experimental.getBestPeaks( 
            {
                from:zoom.from,
                to:zoom.to,
                numberSlots:20,
                limit: 50,
                numberCloseSlots: 300,
                searchMonoisotopicRatio: labelPrefs.searchMonoisotopicRatio
            }
        );
        updateAnnotations(bestPeaks);
        break;
    }
    case 'createSimilarityPeakWidth':
        if (value && value.ms && value.ms.similarity) {
            let similarity=value.ms.similarity;
            let em=value.ms.em;
            let annotations=[];
            let halfBottom=value.ms.similarity.width.bottom/2;
            let halfTop=value.ms.similarity.width.top/2;
            annotations.push({
                type:'rect',
                position: [
                    { x: em-halfTop, y: '5px' },
                    { x: em+halfTop, y: '10px'}
                ]
            });
            annotations.push({
                type:'rect',
                position: [
                    { x: em-halfBottom, y: '15px' },
                    { x: em+halfBottom, y: '20px'}
                ]
            });
            API.createData('widthAnnotation', annotations);
        }
        break;
    case 'clearExperimentalSpectrum': {
        emdb.experimentalSpectrum=undefined;
        API.createData('experimentalChart', undefined);
        API.createData('kindAnnotations', undefined);
        API.createData('annotations', undefined);
        API.doAction('deselectRow', undefined);
        API.doAction('calculate', API.getData('preferences'));
        break;
    }
    case 'ResetPreferences': {
        let preferences=API.getData('preferences');
        preferences.resetValue();
        break;
    }
    case 'clickedPeak': {
        let preferences=API.getData('preferences');
        let mass = value.position[0].x;
        preferences.setChildSync(['labelPrefs','shift'], mass.toFixed(5));
        break;
    }
    case 'resetShift': {
        let preferences=API.getData('preferences');
        preferences.setChildSync(['labelPrefs','shift'], 0);

    }
}


function updateAnnotations(bestPeaks) {
    console.time('updateAnnotations');
    if (! emdb) return;
    let experimental=emdb.experimentalSpectrum;
    if (! experimental) return;
    let preferences=API.getData('preferences').resurrect();
    let labelPrefs=preferences.labelPrefs;

    let prefs={};

    // if we are relativeMass 
    prefs.shift=labelPrefs.shift ? -labelPrefs.shift:0;
    prefs.showMF=labelPrefs.showMF;
    prefs.mfColors=labelPrefs.mfColors;
    prefs.mfPrefs={
        maxIterations: 10000000,
        precision: labelPrefs.mfColors.reduce( (a,b)=>Math.max(a,b.limit), 0),
        minCharge: Number.MIN_SAFE_INTEGER,
        maxCharge: Number.MAX_SAFE_INTEGER,
        allowNeutral: true,
        ionizations:preferences.ionizations,
        ranges: labelPrefs.ranges,
        unsaturation: {
            "min": -1,
            "max": 999,
            "onlyInteger": false,
            "onlyNonInteger": false
        }
    };

    API.createData('annotations', 
        experimental.JsGraph.getPeaksAnnotation(bestPeaks, prefs)
    );
    console.timeEnd('updateAnnotations');
}

