                let prefs=API.getData('preferences');
                if (prefs) {
                    prefs.setChildSync( ['peptidic','sequence'], firstPeptide.sequence );                   
                }
            }

            let firstNucleotide=Sequence.getFirstNucleotide(sampleData);
            if (firstNucleotide && firstNucleotide.sequence) {
                let prefs=API.getData('preferences');
                if (prefs) {
                    prefs.setChildSync( ['nucleic','sequence'], firstNucleotide.sequence );                   
                }
            }
        }
        API.createData('preferences', result.preferences);
        break;
    }
    case 'loadResult': {
        let result = await userAnalysisResults.loadResult(this.action.value);
        API.createData('description', result.description);
        API.createData('preferences', result.preferences);
        API.createData('fragments', result.fragments);
        break;
    }
    case 'refreshResults':
        await userAnalysisResults.refresh();
        break;

}

