const fragments=API.getData('fragments');

const annotations=[];

fragments.forEach( fragment => {
    fragment._highlight=fragment.em || fragment.comment || Math.random();
    fragment.color=getColor(fragment.comment,10);
    fragment.modification=fragment.parts.filter(part => part).join(' ');
    let annotation={
        position:[
            {x:fragment.ms.em,y:"1px", dx:"-1px"},
            {x:fragment.ms.em,y:"20px", dx:"0px"}
        ],
        type:"rect",
        fillColor: getColor(fragment.comment,fragment.ms.charge),
        strokeColor: getColor(fragment.comment,fragment.ms.charge),
        _highlight: fragment._highlight,
        info: fragment,
        label: []
    }
    let line=0;
    annotation.label.push(
        {
            text: 'Z:'+fragment.ms.charge,
            color:'grey',
            position: {
                x:fragment.ms.em,
                dx: "2px",
                y: (line*12+10)+"px"
            }
        }
    );
    line++;
    for (let detail of fragment.details) {
        annotation.label.push(
            {
                text: detail.key + detail.nbUnits,
                color: detail.color,
                position: {
                    x:fragment.ms.em,
                    dx: "2px",
                    y: (line*12+10)+"px"
                }
            }
        );
        line++;
    }

    annotations.push(annotation);
});

API.createData('kindAnnotations', annotations);


function getColor(annotation, charge) {
    if (! annotation) return;

    var alpha=1/(Math.abs(charge)||1);
    var type=annotation.replace('cmp-','').charAt(0);
    switch (type) {
        case 'a':
            return "rgba(255,0,255,"+alpha+")";
            break;
        case 'b':
            return "rgba(255,0,0,"+alpha+")";
            break;
        case 'c':
            return "rgba(255,255,0,"+alpha+")";
            break;
        case 'd':
            return "rgba(255,127,127,"+alpha+")";
            break;
        case 'w':
            return "rgba(127,127,255,"+alpha+")";
            break;
        case 'x':
            return "rgba(0,255,255,"+alpha+")";
            break;
        case 'y':
            return "rgba(0,255,0,"+alpha+")";
            break;
        case 'z':
            return "rgba(0,0,255,"+alpha+")";
            break;
    }
}

if (this.action && this.action.name) {
    this.button=this.action.name;
}

console.log('Analyse data: ',this.button)

const emdb=API.cache('emdb');;
const MolecularFormula = API.cache('MolecularFormula');
const IsotopicDistribution=MolecularFormula.IsotopicDistribution;

const massData = API.cache('massData');

switch (this.button) {
    case 'loadSpectrum':
        loadSpectrum(massData);
        this.hideButton('calibrateAndRecalculate');
        return;
    case 'calculate':
        loadSpectrum(massData);
        break;
    case 'calibrateAndRecalculate':
        const shiftFunction=API.cache('shiftFunction');
        if (! shiftFunction) {
            API.showNotification('Unable to recalibrate the spectrum, no function available.')
            return;
        }
        let x=massData.x.map( x => x-shiftFunction(x) );
        loadSpectrum({
            x,
            y: massData.y
        })

        break;
}


const preferences=API.getData('preferences').resurrect();
const kind=preferences.sequenceKind;

let filter=JSON.parse(JSON.stringify(preferences.filter));
if (emdb.experimentalSpectrum && preferences.mass.threshold) {
    let peaks=emdb.experimentalSpectrum.getPeaks({threshold: preferences.mass.threshold});
    filter.precision=preferences.mass.precision;
    filter.targetMasses=peaks.map( peak => peak.x );
    filter.targetIntensities=peaks.map( peak => peak.y );
}

let similarity = preferences.mass.similarity;

let monomers=preferences.parts.map( part => '('+part.mf+')'+part.min+'-'+part.max);

let mfsArray=[];
if (preferences.terminalA) mfsArray.push(preferences.terminalA);
mfsArray.push(...monomers);
if (preferences.terminalB) mfsArray.push(preferences.terminalB);

emdb.fromArray(mfsArray, {
    ionizations:preferences.ionizations,
    filter
});
let fragments=emdb.get('generated');
if (preferences.mass.minSimilarity>0 && emdb.experimentalSpectrum) {

    if (similarity.widthFunction) {
        similarity.widthFunction =
            'var error = '+preferences.mass.precision+' * mass * 1e-6;\n' + 
            similarity.widthFunction;
    }

    fragments = emdb.searchSimilarity({
        flatten: true,
        minSimilarity: preferences.mass.minSimilarity,
        filter,
        similarity
    });
}

fragments.sort( (a,b) => a.em-b.em );

let totalQuantity=0;

let groupedFragments=[];
let groupedFragment;

const sameEMcounter={};
fragments.forEach( fragment => {
    let em=Number(fragment.em);
    if (! sameEMcounter[em]) {
        groupedFragment={
            em,
            fragments:[],
            quantity: 0,
            _highlight: em,
            mfs: {}
        }
        groupedFragments.push(groupedFragment);
        sameEMcounter[em]={
            count: 0,
            quantity: 0
        };
    }
    groupedFragment.fragments.push(fragment);
    if (fragment.ms && fragment.ms.similarity) {
        totalQuantity+=fragment.ms.similarity.quantity;
        sameEMcounter[em].quantity+=fragment.ms.similarity.quantity;
        groupedFragment.quantity+=fragment.ms.similarity.quantity;
        groupedFragment.mfs[fragment.parts.join(" ")]=true;
    }
    sameEMcounter[em].count++;
    fragment.intensity=fragment.ms && fragment.ms.target ? fragment.ms.target.intensity : '';
});

groupedFragments.forEach( (groupedFragment) => groupedFragment.mfs = Object.keys(groupedFragment.mfs))

fragments.forEach( fragment => {
    fragment.group=sameEMcounter[fragment.em];
});

// add highlight
fragments.forEach( fragment => {
    fragment._highlight=fragment.em || fragment.comment || Math.random();
})

let entities=preferences.parts.map( part => {
    let info=new MolecularFormula.MF(part.mf).getInfo();
    return {
        key: part.key,
        color: part.color,
        mf: part.mf,
        query: '('+part.mf+')',
        mw: info.mass,
        em: info.monoisotopicMass
    };
});

// we will use a trick to construct a type by making search replace
for (let fragment of fragments) {
    fragment.type='';
    fragment.details=[];
    let totalUnits=0;
    for (let part of fragment.parts) {
        if (part) {
            for (let entity of entities) {
                if (part.indexOf(entity.query)===0) {
                    let nbUnits=Number(part.replace(entity.query,''));
                    totalUnits+=nbUnits;
                    fragment.type+=entity.key+nbUnits;
                    fragment.details.push({
                        key: entity.key,
                        color: entity.color,
                        nbUnits
                    })
                }
            }
        }
    }
    fragment.totalUnits=totalUnits;
}

let maxUnits=Math.max(...fragments.map( fragment => fragment.totalUnits));
for (let fragment of fragments) {
    fragment.indicator=fragment.details.map( (detail) => {
        return {
          "bgcolor": detail.color,
          "size": detail.nbUnits/maxUnits 
        }
    });
    fragment.indicator.push({
        bgcolor: 'white',
        size: (maxUnits-fragment.totalUnits)/maxUnits
    })
}

API.createData('fragments', fragments);
API.createData('groupedFragments', groupedFragments);

API.createData('totalQuantity',totalQuantity);

let chargedFragments=fragments.filter(fragment => Number(fragment.ms.charge)!==0);

let isotopicDistribution=new IsotopicDistribution(chargedFragments);
var data=isotopicDistribution.getXY();


