})

function loadSpectrum(massData) {
    if (! massData) return;
    emdb.setExperimentalSpectrum(massData);
    emdb.experimentalSpectrum.scaleY(100);
    let data=emdb.experimentalSpectrum.data;
    let totalPeaks=data.y.reduce( (previous, current) => previous+current, 0);

    let peaksWidth=emdb.experimentalSpectrum.peaksWidth();
    API.cache('peaksWidth', peaksWidth);

    if (peaksWidth.score.r2>0) {
        // hack because of font awesome / spectra displayer problem
        let peaks={x:[],y:[]};
        let length=peaksWidth.widths.x.length;

        if (length>500) {
            let step=Math.floor(length/500);
            for (let i=0; i<length; i+=step) {
                peaks.x.push(peaksWidth.widths.x[i]);
                peaks.y.push(peaksWidth.widths.y[i]);
            }
        } else {
            peaks=peaksWidth.widths;
        }

        API.createData('peaksWidths', peaks);
        API.createData('peaksWidthFit', peaksWidth.fit);
        API.createData('peaksWidthR2', peaksWidth.score.r2);
        API.createData('peaksWidthTex', peaksWidth.tex);
    } else {
        UI.showNotification('Could not calculate peaks width','warning');
        API.createData('peaksWidths', undefined);
        API.createData('peaksWidthFit', undefined);
        API.createData('peaksWidthR2', undefined);
        API.createData('peaksWidthTex', undefined);
    }

    API.createData('experimentalChart', data);
