function generateDefectsColor(defects, ratio) {
    let defectsColor=[];
    for (let defect of defects) {
        let mfs=defect.mf.split(/[ ,;.]+/);
        for (let mf of mfs) {
            const em = new MolecularFormula.MF(mf).getInfo().monoisotopicMass*ratio;
            defectsColor.push({
                defect: Math.round(em)-em,
                color: defect.color
            })
        }
    }
    return defectsColor;
}

function findBestColor(defectsColor, massDefect, defaultColor, tolerance) {
    let minDefect = Number.MAX_VALUE;
    let currentColor=defaultColor;
    for (let defectColor of defectsColor) {
        let currentDiff=Math.abs(defectColor.defect-massDefect);
        if (currentDiff<minDefect && currentDiff<tolerance) {
            minDefect=currentDiff;
            currentColor=defectColor.color
        }
    }
    return currentColor;
}

