            });
            API.cache('sample',sample);
            await sample.waitSampleLoaded();

            var roc = new Roc(data.message.couchDB);
            API.cache('roc', roc);

            var userAnalysisResults = new UserAnalysisResults(roc, data.message.uuid);
            API.cache("userAnalysisResults", userAnalysisResults);
            userAnalysisResults.refresh();
        }
    }
} else {
    var {extSample} = await API.require('vh/eln/SampleFactory');
    var sample = await extSample();
    API.cache('sample',sample);
    API.createData('LoadDemos','');
}



